insert into drafts values ('5ba8852703579766846d4568','5c657cd4fffc5157aa87b64d','5caca6c5917c5e550894ff1e','A NAIRU/NAWRU de uma economia é a taxa de desemprego:','5c94e110e81d930eb8cf47c6','compatível com a estabilidade da taxa de crescimento do PIB.')
insert into drafts values ('5ba8852703579766846d4568','5c657cd4fffc5157aa87b64d','5caca6c5917c5e550894ff1e','A NAIRU/NAWRU de uma economia é a taxa de desemprego:','5c94e110e81d930eb8cf47c7','de curto prazo. ')
