insert into drafts values ('5ba8852703579766846d4568','5c657cd4fffc5157aa87b64d','5caca6c5917c5e550894ff22','O CAGED (Cadastro Geral de Empregados e Desempregados) divulgado pelo ministério do trabalho tem como tema:','5c94e110e81d930eb8cf47ed','fluxos de admissão e desligamento de trabalhadores regidos pela CLT, com os respectivos salários, com detalhamento por região  e setor econômico')
