insert into drafts values ('5ba8852703579766846d4568','5c657cd4fffc5157aa87b64d','5caca6c5917c5e550894ff23','Considerando a Curva de Phillips, uma política monetária expansionista, resulta, no curto prazo em:','5ca3ee2e96f60a734799d43d','redução dos preços e elevação do desemprego')
