insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5ac','A respeito da taxa Selic, pode-se afirmar que:','5c6c3fb64461d86a7324a5a8','É a taxa média das operações de câmbio apuradas no Sisbacen ao longo de um dia')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5ac','A respeito da taxa Selic, pode-se afirmar que:','5c6c3fb64461d86a7324a5a9','É a taxa média ajustada dos financiamentos diários com lastro em títulos federais, apurados no Sistema Especial de Liquidação e Custódia')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5ac','A respeito da taxa Selic, pode-se afirmar que:','5c6c3fb64461d86a7324a5aa','É a taxa paga pelos bancos ao se capitalizarem junto a outros bancos')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5ac','A respeito da taxa Selic, pode-se afirmar que:','5c6c3fb64461d86a7324a5ab','Gastos com assinatura de jornais, provisão para devedores duvidosos e seguros')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b1','A respeito do BACEN:','5c6c3fb64461d86a7324a5ad','É uma instituição que tem como o ministro da fazenda como principal integrante, cuja a principal tarefa é definir a meta da taxa SELIC através de reuniões do COPOM o conselho que constitui a instituição')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b1','A respeito do BACEN:','5c6c3fb64461d86a7324a5ae','É uma instituição do SFN que tem como o ministro da fazenda como principal integrante, cuja a principal tarefa é definir a meta de inflação através de reuniões do COPOM conselho que compõe a instituição')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b1','A respeito do BACEN:','5c6c3fb64461d86a7324a5af','É uma instituição do SFN que tem como principal figura a trinidade econômica( Ministro da fazenda, Planejamento e presidente do BACEN) que participa do COPOM definindo a meta de inflação')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b1','A respeito do BACEN:','5c6c3fb64461d86a7324a5b0','É um um Comitê ligado ao CMN que tem como função controlar a meta de inflação')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b6','A respeito do SFN, marque a alternativa que mostra qual instituição tem a obrigação de supervisionar os bancos?','5c6c3fb64461d86a7324a5b2','Bacen')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b6','A respeito do SFN, marque a alternativa que mostra qual instituição tem a obrigação de supervisionar os bancos?','5c6c3fb64461d86a7324a5b3','CVM')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b6','A respeito do SFN, marque a alternativa que mostra qual instituição tem a obrigação de supervisionar os bancos?','5c6c3fb64461d86a7324a5b4','CMN')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5b6','A respeito do SFN, marque a alternativa que mostra qual instituição tem a obrigação de supervisionar os bancos?','5c6c3fb64461d86a7324a5b5','SUSEP')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5bb','A respeito dos títulos públicos, é correto afirmar que:','5c6c3fb64461d86a7324a5b7','São emitidos somente quando o país possui déficit comercial')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5bb','A respeito dos títulos públicos, é correto afirmar que:','5c6c3fb64461d86a7324a5b8','São conceitualmente títulos que remuneram quem os compra por meio de uma rentabilidade pré ou pós-fixada')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5bb','A respeito dos títulos públicos, é correto afirmar que:','5c6c3fb64461d86a7324a5b9','Pessoas físicas só podem adquirir títulos públicos por cotas em fundos de investimento. ')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5bb','A respeito dos títulos públicos, é correto afirmar que:','5c6c3fb64461d86a7324a5ba','O Banco Central não pode adquirir títulos públicos do Tesouro Nacional')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c0','A taxa normalmente considerada como livre de risco ou de menor risco da economia é aquela que:','5c6c3fb64461d86a7324a5bc','É obtida por investimentos em ações blue chips')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c0','A taxa normalmente considerada como livre de risco ou de menor risco da economia é aquela que:','5c6c3fb64461d86a7324a5bd','O governo federal paga por seus títulos emitidos')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c0','A taxa normalmente considerada como livre de risco ou de menor risco da economia é aquela que:','5c6c3fb64461d86a7324a5be','o Sistema Financeiro da Habitação utiliza para cálculo dos reajustes de seus contratos')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c0','A taxa normalmente considerada como livre de risco ou de menor risco da economia é aquela que:','5c6c3fb64461d86a7324a5bf','as instituições financeiras captam e emprestam recursos entre si')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c5','As corretoras de títulos e valores mobiliários estão autorizadas','5c6c3fb64461d86a7324a5c1','Realizarem operações de arredamento mercantil ')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c5','As corretoras de títulos e valores mobiliários estão autorizadas','5c6c3fb64461d86a7324a5c2','Não podem administrar recursos de terceiros')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c5','As corretoras de títulos e valores mobiliários estão autorizadas','5c6c3fb64461d86a7324a5c3','Podem captar através de depósito à vista')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5c5','As corretoras de títulos e valores mobiliários estão autorizadas','5c6c3fb64461d86a7324a5c4','São autorizadas a atuarem no mercado de câmbio')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5ca','As instituições financeiras não monetárias:','5c6c3fb64461d86a7324a5c6','incluem os bancos comerciais')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5ca','As instituições financeiras não monetárias:','5c6c3fb64461d86a7324a5c7','incluem as cooperativas de crédito')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5ca','As instituições financeiras não monetárias:','5c6c3fb64461d86a7324a5c8','incluem as caixas econômicas.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5ca','As instituições financeiras não monetárias:','5c6c3fb64461d86a7324a5c9','captam recursos através da emissão de títulos.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a5cf','As instituições que compõem o Sistema Financeiro Nacional (SFN) devem permitir, dentro das melhores condições possíveis:','5c6c3fb64461d86a7324a5cb','A realização dos fluxos de fundos entre tomadores e poupadores de recursos para a economia.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a5cf','As instituições que compõem o Sistema Financeiro Nacional (SFN) devem permitir, dentro das melhores condições possíveis:','5c6c3fb64461d86a7324a5cc','O saneamento dos bancos em condições duvidosas de questionamento, funcionando como um fundo de reserva.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a5cf','As instituições que compõem o Sistema Financeiro Nacional (SFN) devem permitir, dentro das melhores condições possíveis:','5c6c3fb64461d86a7324a5cd','A livre negociação entre os países membros do Mercosul, como forma de preparação da Área de Livre Comércio das Américas (Alca).')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a5cf','As instituições que compõem o Sistema Financeiro Nacional (SFN) devem permitir, dentro das melhores condições possíveis:','5c6c3fb64461d86a7324a5ce','A absorção dos recursos disponíveis pelo mercado para investimentos estrangeiros no país')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5d4','Assinale a alternativa correta quanto ao universo dos bancos de investimentos:','5c6c3fb64461d86a7324a5d0',' Bancos de investimento têm sua atividade principal baseada em captação de depósitos à vista.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5d4','Assinale a alternativa correta quanto ao universo dos bancos de investimentos:','5c6c3fb64461d86a7324a5d1','Bancos de investimento são responsáveis pela engenharia financeira por trás de M&A e IPOs, por exemplo.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5d4','Assinale a alternativa correta quanto ao universo dos bancos de investimentos:','5c6c3fb64461d86a7324a5d2','Não é permitido que um banco de investimento utilize como funding recursos captados por meio de abertura de capital.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5d4','Assinale a alternativa correta quanto ao universo dos bancos de investimentos:','5c6c3fb64461d86a7324a5d3',' Bancos de investimento podem fazer processos de M&A, mas não têm como atividade a emissão de títulos de dívida privada, pois esses configuram captação de depósitos à vista.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5d9','BACEN tem como atribuições ou funções os seguintes itens:<br /><br />I. Adaptar o volume dos meios de pagamento às reais necessidades da economia; regular o valor interno e externo da moeda e o equilíbrio do balanço de pagamentos; orientar a aplicação dos recursos das instituições financeiras; propiciar o aperfeiçoamento das instituições e dos instrumentos financeiros; zelar pela liquidez e solvência das instituições financeiras; coordenar as políticas monetária, creditícia, orçamentária e da dívida pública interna e externa.<br />II. Emitir papel-moeda e moeda metálica; executar os serviços do meio circulante; receber recolhimentos compulsórios e voluntários das instituições financeiras e bancárias; realizar operações de redesconto e empréstimo às instituições financeiras;<br />III. Regular a execução dos serviços de compensação de cheques e outros papéis; efetuar operações de compra e venda de títulos públicos federais; exercer o controle de crédito; exercer a fiscalização das instituições financeiras;<br />IV. Autorizar o funcionamento das instituições financeiras; estabelecer as condições para o exercício de quaisquer cargos de direção nas instituições financeiras; vigiar a interferência de outras empresas nos mercados financeiros e de capitais; controlar o fluxo de capitais estrangeiros no país<br />','5c6c3fb64461d86a7324a5d5','I, II e III')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5d9','BACEN tem como atribuições ou funções os seguintes itens:<br /><br />I. Adaptar o volume dos meios de pagamento às reais necessidades da economia; regular o valor interno e externo da moeda e o equilíbrio do balanço de pagamentos; orientar a aplicação dos recursos das instituições financeiras; propiciar o aperfeiçoamento das instituições e dos instrumentos financeiros; zelar pela liquidez e solvência das instituições financeiras; coordenar as políticas monetária, creditícia, orçamentária e da dívida pública interna e externa.<br />II. Emitir papel-moeda e moeda metálica; executar os serviços do meio circulante; receber recolhimentos compulsórios e voluntários das instituições financeiras e bancárias; realizar operações de redesconto e empréstimo às instituições financeiras;<br />III. Regular a execução dos serviços de compensação de cheques e outros papéis; efetuar operações de compra e venda de títulos públicos federais; exercer o controle de crédito; exercer a fiscalização das instituições financeiras;<br />IV. Autorizar o funcionamento das instituições financeiras; estabelecer as condições para o exercício de quaisquer cargos de direção nas instituições financeiras; vigiar a interferência de outras empresas nos mercados financeiros e de capitais; controlar o fluxo de capitais estrangeiros no país<br />','5c6c3fb64461d86a7324a5d6','II, III  e IV')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5d9','BACEN tem como atribuições ou funções os seguintes itens:<br /><br />I. Adaptar o volume dos meios de pagamento às reais necessidades da economia; regular o valor interno e externo da moeda e o equilíbrio do balanço de pagamentos; orientar a aplicação dos recursos das instituições financeiras; propiciar o aperfeiçoamento das instituições e dos instrumentos financeiros; zelar pela liquidez e solvência das instituições financeiras; coordenar as políticas monetária, creditícia, orçamentária e da dívida pública interna e externa.<br />II. Emitir papel-moeda e moeda metálica; executar os serviços do meio circulante; receber recolhimentos compulsórios e voluntários das instituições financeiras e bancárias; realizar operações de redesconto e empréstimo às instituições financeiras;<br />III. Regular a execução dos serviços de compensação de cheques e outros papéis; efetuar operações de compra e venda de títulos públicos federais; exercer o controle de crédito; exercer a fiscalização das instituições financeiras;<br />IV. Autorizar o funcionamento das instituições financeiras; estabelecer as condições para o exercício de quaisquer cargos de direção nas instituições financeiras; vigiar a interferência de outras empresas nos mercados financeiros e de capitais; controlar o fluxo de capitais estrangeiros no país<br />','5c6c3fb64461d86a7324a5d7','I apenas')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5d9','BACEN tem como atribuições ou funções os seguintes itens:<br /><br />I. Adaptar o volume dos meios de pagamento às reais necessidades da economia; regular o valor interno e externo da moeda e o equilíbrio do balanço de pagamentos; orientar a aplicação dos recursos das instituições financeiras; propiciar o aperfeiçoamento das instituições e dos instrumentos financeiros; zelar pela liquidez e solvência das instituições financeiras; coordenar as políticas monetária, creditícia, orçamentária e da dívida pública interna e externa.<br />II. Emitir papel-moeda e moeda metálica; executar os serviços do meio circulante; receber recolhimentos compulsórios e voluntários das instituições financeiras e bancárias; realizar operações de redesconto e empréstimo às instituições financeiras;<br />III. Regular a execução dos serviços de compensação de cheques e outros papéis; efetuar operações de compra e venda de títulos públicos federais; exercer o controle de crédito; exercer a fiscalização das instituições financeiras;<br />IV. Autorizar o funcionamento das instituições financeiras; estabelecer as condições para o exercício de quaisquer cargos de direção nas instituições financeiras; vigiar a interferência de outras empresas nos mercados financeiros e de capitais; controlar o fluxo de capitais estrangeiros no país<br />','5c6c3fb64461d86a7324a5d8','II e III')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5de','Com relação ao mercado de crédito, não podemos afirmar:','5c6c3fb64461d86a7324a5da','Constituem negócios creditícios típicos as operações ativas das instituições financeiras, como a abertura de crédito.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5de','Com relação ao mercado de crédito, não podemos afirmar:','5c6c3fb64461d86a7324a5db','Constituem negócios creditícios típicos as operações passivas, como as de colocação de certificados bancários (CDB) e operações de curto prazo, com títulos públicos.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5de','Com relação ao mercado de crédito, não podemos afirmar:','5c6c3fb64461d86a7324a5dc','Constituem negócios creditícios típicos as operações passivas, como as de aceite de letras de câmbio')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a5de','Com relação ao mercado de crédito, não podemos afirmar:','5c6c3fb64461d86a7324a5dd','Constituem negócios creditícios típicos as operações passivas, como as de emissão de outros papéis por parte das instituições financeiras.<br /><br />')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5e3','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5c6c3fb64461d86a7324a5df','As operações contratadas entre o BC e o Tesouro tenha um lastro. ')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5e3','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5c6c3fb64461d86a7324a5e0','efetue a emissão, o resgate, o pagamento dos juros e a custódia dos títulos sob sua responsabilidade.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5e3','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5c6c3fb64461d86a7324a5e1','seja composto, entre outros, por títulos da dívida externa brasileira.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a5e3','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5c6c3fb64461d86a7324a5e2','eja composto, entre outros, por títulos bancários transacionados na CETIP')
