insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a66f','Qual a função básica e o objetivo de uma instituição financeira e quais são seus três elementos?','5c6c3fb64461d86a7324a66b','A função básica de uma instituição financeira é a intermediação entre poupadores e devedores sendo seus três elementos: montante,  taxa e o prazo')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a66f','Qual a função básica e o objetivo de uma instituição financeira e quais são seus três elementos?','5c6c3fb64461d86a7324a66c','A função básica de uma instituição financeira é servir de poupador de dinheiro sendo seus três elementos: a taxa, o prazo e o notionnal')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a66f','Qual a função básica e o objetivo de uma instituição financeira e quais são seus três elementos?','5c6c3fb64461d86a7324a66d','A função básica de uma instituição financeira é servir de credor de capital para outras instituições, financiando assim o país. Seus três elementos básicos são respectivamente: a taxa, o montante e o notionnal')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f6','O Sistema Financeiro Nacional (SFN)','5c6c3fb64461d86a7324a66f','Qual a função básica e o objetivo de uma instituição financeira e quais são seus três elementos?','5c6c3fb64461d86a7324a66e','nenhuma das alternativas')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a674','Qual a maior instituição do SFN e qual a respectiva função dela?','5c6c3fb64461d86a7324a670','O CMN cuja a função é meramente de diretrizes de políticas que do país deve adotar e normativas não tendo função executiva, composto pela trindade econômica ministro da fazenda, o presidente do Bacen e o ministro do planejamento')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a674','Qual a maior instituição do SFN e qual a respectiva função dela?','5c6c3fb64461d86a7324a671','O BACEN, é a mais importante instituição do SFN, tendo como funçãodefinir a meta da SELIC, da inflação e de supervisão de outras instituições')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a674','Qual a maior instituição do SFN e qual a respectiva função dela?','5c6c3fb64461d86a7324a672','A CVM é a instituição mais importante do SFN, tendo como função regular e supervisionar as entidades ligadas ao SFN')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a674','Qual a maior instituição do SFN e qual a respectiva função dela?','5c6c3fb64461d86a7324a673','O CMN é o orgão mais importante do SFN, tendo como função supervisionar e organizar o SFN controlando a meta de inflação e a taxa de juros')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a679','Qual a principal implicação de um regime de câmbio fixo?','5c6c3fb64461d86a7324a675','Um país de câmbio fixo, por mais que sua moeda esteja cotada oficialmente muito valorizada em relação as outras terá uma inflação mais alta, refletindo em diferentes preços que não "casam" com a cotação e consumindo as reservas do país')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a679','Qual a principal implicação de um regime de câmbio fixo?','5c6c3fb64461d86a7324a676','Um país de câmbio fixo terá sua moeda "travada" tendo então uma inflação mais controlada em relação a países de câmbio flutuante.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a679','Qual a principal implicação de um regime de câmbio fixo?','5c6c3fb64461d86a7324a677','Um país de câmbio fixo terá uma inflação maior que um país de câmbio flutuante, pois suas reservas financeiras não serão usadas para cobrir a paridade da moeda perante a outra.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a679','Qual a principal implicação de um regime de câmbio fixo?','5c6c3fb64461d86a7324a678','Nenhuma das alternativas')
