insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6ab','Sistema Financeiro Nacional (SFN) é composto por um conjunto de instituições financeiras públicas e privadas, e seu órgão máximo é:','5c6c3fb64461d86a7324a6a8','Banco Central do Brasil (BCB)')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6ab','Sistema Financeiro Nacional (SFN) é composto por um conjunto de instituições financeiras públicas e privadas, e seu órgão máximo é:','5c6c3fb64461d86a7324a6a9','A Comissão de Valores Mobiliários (CVM)')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6ab','Sistema Financeiro Nacional (SFN) é composto por um conjunto de instituições financeiras públicas e privadas, e seu órgão máximo é:','5c6c3fb64461d86a7324a6aa','Comitê de Política Econômica (Copom)')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b0','Sob qual instituição reguladora no SFN, o mercado financeiro está sob regulamento?','5c6c3fb64461d86a7324a6ac','CVM')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b0','Sob qual instituição reguladora no SFN, o mercado financeiro está sob regulamento?','5c6c3fb64461d86a7324a6ad','COPOM')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b0','Sob qual instituição reguladora no SFN, o mercado financeiro está sob regulamento?','5c6c3fb64461d86a7324a6ae','BNDES')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b0','Sob qual instituição reguladora no SFN, o mercado financeiro está sob regulamento?','5c6c3fb64461d86a7324a6af','SUSEP')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b5','Sobre a CVM:','5c6c3fb64461d86a7324a6b1','É a instituição que fiscaliza o mercado de valores mobiliários no Brasil e o mercado financeiro, estando sujeita as normas do CMN e regulação do BACEN')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b5','Sobre a CVM:','5c6c3fb64461d86a7324a6b2','É a instituição que fiscaliza o mercado de valores imobiliários no Brasil e o mercado financeiro, estando sujeita as nomas do BACEN e a regulação do CMN')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b5','Sobre a CVM:','5c6c3fb64461d86a7324a6b3','É a instituição que coloca diretrizes e normas para o mercado de valores mobiliários brasileiro, estando sujeita as normas do BACEN.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6b5','Sobre a CVM:','5c6c3fb64461d86a7324a6b4','nenhuma das alternativas')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ba','Sobre mercado de capitais:','5c6c3fb64461d86a7324a6b6','Mercado de Capitais visa realizar a troca entre os agentes superavitários e o deficitário por meio de um intermediador.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ba','Sobre mercado de capitais:','5c6c3fb64461d86a7324a6b7','O mercado de Capitais é a troca direta entre agentes superavitários e o deficitário')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ba','Sobre mercado de capitais:','5c6c3fb64461d86a7324a6b8','O mercado de Capitais é a troca entre um financiador(banco) e os agentes deficitários')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ba','Sobre mercado de capitais:','5c6c3fb64461d86a7324a6b9','O mercado de Capitais é o local onde ocorre os empréstimos e financiamentos para um agente deficitárioi ou até superavitário com a necessidade de financiamento')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6bf','Sobre moeda escritural é correto afirmar que:','5c6c3fb64461d86a7324a6bb','É uma moeda que fica depositada no banco com lastro fiduciário. Esta moeda é a principal responsável pelo efeito multiplicador de crédito.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6bf','Sobre moeda escritural é correto afirmar que:','5c6c3fb64461d86a7324a6bc','É uma moeda que fica depositada no banco na qual tem um lastro fiduciário. Esta moeda é a principal responsável pelo compulsório no Banco central')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6bf','Sobre moeda escritural é correto afirmar que:','5c6c3fb64461d86a7324a6bd','É uma moeda que fica depositada no BACEN por meio de compulsórios que tem um lastro nos Bancos comerciais/Múltiplos. Esta moeda é a principal responsável pelo efeito multiplicador de crédito')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6bf','Sobre moeda escritural é correto afirmar que:','5c6c3fb64461d86a7324a6be','É uma moeda que fica depositada no banco com lastro fiduciário. Esta moeda é a principal responsável pelas reservas partilhadas.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c4','Sobre o Conselho Monetário Nacional (CMN), NÃO podemos afirmar que:','5c6c3fb64461d86a7324a6c0','Tem a função fiscalizadora e disciplinadora do mercado financeiro, ao definir as regras, limites e condutas das instituições financeiras')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c4','Sobre o Conselho Monetário Nacional (CMN), NÃO podemos afirmar que:','5c6c3fb64461d86a7324a6c1','É um órgão eminentemente normativo, não desempenhando nenhuma atividade executiva.<br />')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c4','Sobre o Conselho Monetário Nacional (CMN), NÃO podemos afirmar que:','5c6c3fb64461d86a7324a6c2','Sua finalidade principal é a formulação de toda a politica de moeda e do credito')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c4','Sobre o Conselho Monetário Nacional (CMN), NÃO podemos afirmar que:','5c6c3fb64461d86a7324a6c3','Processa todo controle do sistema financeiro, influenciando as ações de órgãos normativos como o BNDES, por exemplo')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c9','Sobre o COPOM é correto afirmar:','5c6c3fb64461d86a7324a6c5','É um comitê integrante do BACEN que tem como tarefa estabelecer a taxa de juros, para que se chegue a taxa de juros (SELIC) estipulada pelo CMN.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c9','Sobre o COPOM é correto afirmar:','5c6c3fb64461d86a7324a6c6','É um comitê que tem como participantes a trinidade econômica(ministro da fazenda, planejamento e presidente do BACEN) que participa no CMN, definindo a taxa de juros(SELIC) para atingir a inflação.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c9','Sobre o COPOM é correto afirmar:','5c6c3fb64461d86a7324a6c7','É um comitê participante na CVM, que julga as ações que visam regular o mercado financeiro')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a6c9','Sobre o COPOM é correto afirmar:','5c6c3fb64461d86a7324a6c8','É um comitê ligado ao BACEN, formado por várias pessoas que visa estabelecer uma meta de inflação')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ce','Sobre o Mercado secundário, pode-se afirmar:','5c6c3fb64461d86a7324a6ca','O local onde são realizadas as diversas transações de compra e venda de ações registradas em Bolsas de Valores é denominado de mercado de balcão')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ce','Sobre o Mercado secundário, pode-se afirmar:','5c6c3fb64461d86a7324a6cb','No mercado de balcão organizado são negociados somente ações de companhias abertas')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ce','Sobre o Mercado secundário, pode-se afirmar:','5c6c3fb64461d86a7324a6cc','Toda a transação em títulos não irá diretamente pro caixa do emissor')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6ce','Sobre o Mercado secundário, pode-se afirmar:','5c6c3fb64461d86a7324a6cd','Em geral, o mercado de balcão tem um local físico para a realização dos negócios entre as instituições, como sociedades corretoras, sociedades distribuidoras e bancos de investimento')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6d3','Sobre os títulos do mercado monetário:','5c6c3fb64461d86a7324a6cf','São títulos de curto prazo como notas promissórias e CDB''S de instituições financeiras.')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6d3','Sobre os títulos do mercado monetário:','5c6c3fb64461d86a7324a6d0','São títulos de curto prazo como CRI/CRA e LFT')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6d3','Sobre os títulos do mercado monetário:','5c6c3fb64461d86a7324a6d1','São títulos de curto/ médio prazo como Debêntures e CDB''S')
insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f8','Mercados do SFN','5c6c3fb64461d86a7324a6d3','Sobre os títulos do mercado monetário:','5c6c3fb64461d86a7324a6d2','São direitos de negociação de moedas como Dólar e Real')
