insert into bdq_old_04_02 values ('5ba55300e5697560fde931f4','Mercado Financeiro e o Sistema Financeiro Nacional','5ba554f2e5697560fde931f7','Instituições do SFN','5c6c3fb64461d86a7324a714','O Banco Central do Brasil tem como atribuição:','5c6c3fb64461d86a7324a710','Receber os recolhimentos compulsórios dos bancos')
