insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e5','O Regime cambial que implica  importação da estabilidade monetária do país ao qual se atrela o câmbio é:','5c94e110e81d930eb8cf473e','Câmbio fixo')
