insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e5','O Regime cambial que implica  importação da estabilidade monetária do país ao qual se atrela o câmbio é:','5c94e110e81d930eb8cf4740','Banda Cambial')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e5','O Regime cambial que implica  importação da estabilidade monetária do país ao qual se atrela o câmbio é:','5c94e110e81d930eb8cf4741','Âncora monetária')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e6','Em um regime cambial flutuante puro, o banco central:','5c94e110e81d930eb8cf4743','não intervém no mercado de câmbio, permanecendo inalteradas as reservas internacionais.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e6','Em um regime cambial flutuante puro, o banco central:','5c94e110e81d930eb8cf4744','atua passivamente, devendo ter um montante de reservas internacionais maior do que no regime cambial fixo.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e6','Em um regime cambial flutuante puro, o banco central:','5c94e110e81d930eb8cf4745','utiliza as reservas internacionais com vistas a resguardar o pais de quaisquer variações na taxa de câmbio.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e6','Em um regime cambial flutuante puro, o banco central:','5c94e110e81d930eb8cf4746','busca reduzir a volatilidade da taxa de câmbio efetiva (multilateral).')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e7','O atual regime de câmbio brasileiro é um:','5c94e110e81d930eb8cf4748','regime de câmbio flutuante, em que o Banco Central pode fazer intervenções cambiais quando o mercado está disfuncional.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e7','O atual regime de câmbio brasileiro é um:','5c94e110e81d930eb8cf4749','regime de câmbio fixo')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e7','O atual regime de câmbio brasileiro é um:','5c94e110e81d930eb8cf474a','regime de câmbio flutuante, que não permite a intervenção do Banco Central do Brasil.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e7','O atual regime de câmbio brasileiro é um:','5c94e110e81d930eb8cf474b','regime de câmbio por bandas cambiais, o qual permite que o câmbio oscile dentro de um intervalo')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e8','Assinale a que NÃO está em acordo no que se refere a regimes cambiais:','5c94e110e81d930eb8cf474d','No regime de câmbio flutuante o Banco Central determina valor da taxa de câmbio. ')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e8','Assinale a que NÃO está em acordo no que se refere a regimes cambiais:','5c94e110e81d930eb8cf474e','No regime de câmbio fixo importa-se a estabilidade monetária do país ao qual se atrela o câmbio')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e8','Assinale a que NÃO está em acordo no que se refere a regimes cambiais:','5c94e110e81d930eb8cf474f','O regime de bandas cambiais é um exemplo de soft peg')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e8','Assinale a que NÃO está em acordo no que se refere a regimes cambiais:','5c94e110e81d930eb8cf4750','No regime de câmbio flutuante, o Banco Central não intervem (regime puro) ou intervem quando o mercado está disfuncional.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e9','A partir de janeiro de 1999, o Brasil optou por um regime de câmbio flutuante. Em relação a essa mudança podemos comentar que:','5c94e110e81d930eb8cf4752','decorreu das dificuldades do Governo em manter o regime cambial anterior devido à sua incapacidade de absorver choques e à perspectiva de exaustão de reservas. ')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e9','A partir de janeiro de 1999, o Brasil optou por um regime de câmbio flutuante. Em relação a essa mudança podemos comentar que:','5c94e110e81d930eb8cf4753','Iniciou-se com a decisão do Banco Central de promover uma desvalorização controlada de 50% do real contra o dólar americano.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e9','A partir de janeiro de 1999, o Brasil optou por um regime de câmbio flutuante. Em relação a essa mudança podemos comentar que:','5c94e110e81d930eb8cf4754','caracterizou-se pela implantação de bandas cambiais. ')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540e9','A partir de janeiro de 1999, o Brasil optou por um regime de câmbio flutuante. Em relação a essa mudança podemos comentar que:','5c94e110e81d930eb8cf4755','ocorreu devido ao desejo do BACEN por referenciar a moeda local a uma cesta de moedas estrangeiras.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540ea','Por meio dos contratos de “swap cambial”, o BC realiza uma operação que equivale à uma venda de moeda no mercado futuro (derivativos), aumentado assim a disponibilidade de hedge cambial e reduzindo a pressão sobre a alta da moeda. O Regime Cambial que NÃ0 é compatível com essa operação é:','5c94e110e81d930eb8cf4757','Câmbio flutuante puro')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540ea','Por meio dos contratos de “swap cambial”, o BC realiza uma operação que equivale à uma venda de moeda no mercado futuro (derivativos), aumentado assim a disponibilidade de hedge cambial e reduzindo a pressão sobre a alta da moeda. O Regime Cambial que NÃ0 é compatível com essa operação é:','5c94e110e81d930eb8cf4758','Câmbio flutuante')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540ea','Por meio dos contratos de “swap cambial”, o BC realiza uma operação que equivale à uma venda de moeda no mercado futuro (derivativos), aumentado assim a disponibilidade de hedge cambial e reduzindo a pressão sobre a alta da moeda. O Regime Cambial que NÃ0 é compatível com essa operação é:','5c94e110e81d930eb8cf4759','Câmbio Fixo Puro')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540ea','Por meio dos contratos de “swap cambial”, o BC realiza uma operação que equivale à uma venda de moeda no mercado futuro (derivativos), aumentado assim a disponibilidade de hedge cambial e reduzindo a pressão sobre a alta da moeda. O Regime Cambial que NÃ0 é compatível com essa operação é:','5c94e110e81d930eb8cf475a','Soft peg')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540eb','No início de 1999, o Brasil tinha dificuldade em manter o regime de soft peg que vinha servindo como âncora cambial. O novo presidente do banco central adotou o sistema de Metas de Inflação e a flutuação cambial no Brazil visando minimizar os efeitos do(a):','5c94e110e81d930eb8cf475c','Interrupção abrupta / reversão do financiamento externo para economia brasileira  (sudden stop), que possuía grandes necessidades de financiamento externo. ')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540eb','No início de 1999, o Brasil tinha dificuldade em manter o regime de soft peg que vinha servindo como âncora cambial. O novo presidente do banco central adotou o sistema de Metas de Inflação e a flutuação cambial no Brazil visando minimizar os efeitos do(a):','5c94e110e81d930eb8cf475d','Intervenção no mercado de câmbio.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540eb','No início de 1999, o Brasil tinha dificuldade em manter o regime de soft peg que vinha servindo como âncora cambial. O novo presidente do banco central adotou o sistema de Metas de Inflação e a flutuação cambial no Brazil visando minimizar os efeitos do(a):','5c94e110e81d930eb8cf475e','Aumento insustentável da importação de bens e capitais.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64f','Câmbio e Setor Externo','5c9e6fed3d3bb1636ac540eb','No início de 1999, o Brasil tinha dificuldade em manter o regime de soft peg que vinha servindo como âncora cambial. O novo presidente do banco central adotou o sistema de Metas de Inflação e a flutuação cambial no Brazil visando minimizar os efeitos do(a):','5c94e110e81d930eb8cf475f','Política monetária expansionista, necessária para compensar os efeitos contracionistas do fuga de capitais.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ec','O componente que tem mais peso no IGP-M é:','5c9cea8794da162ea535ca82','IPA (60%)')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ec','O componente que tem mais peso no IGP-M é:','5c9cea8794da162ea535ca83','INCC (55%)')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ec','O componente que tem mais peso no IGP-M é:','5c9cea8794da162ea535ca84','IPCA (65%)')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ec','O componente que tem mais peso no IGP-M é:','5c9cea8794da162ea535ca85','PIB')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ed','O decreto de número 3088, de 21 de junho de 1999 estabelece,  como diretriz para fixação do regime de política monetária, a sistemática de "metas para inflação". Entre as medidas dentro da sistemática estabelicida, destaca-se:','5c94e110e81d930eb8cf4766','as metas e os respectivos intervalos de tolerância serão fixados pelo CMN, mediante proposta do presidente do BACEN.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ed','O decreto de número 3088, de 21 de junho de 1999 estabelece,  como diretriz para fixação do regime de política monetária, a sistemática de "metas para inflação". Entre as medidas dentro da sistemática estabelicida, destaca-se:','5c94e110e81d930eb8cf4767','o COPOM é responsável por definir quais índices de preços serão referência para alcançar a meta de Inflação anualmente.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ed','O decreto de número 3088, de 21 de junho de 1999 estabelece,  como diretriz para fixação do regime de política monetária, a sistemática de "metas para inflação". Entre as medidas dentro da sistemática estabelicida, destaca-se:','5c94e110e81d930eb8cf4768','Compete, exclusivamente, ao secretário especial da Fazenda estabelecer as políticas necessárias para o cumprimento da meta.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ed','O decreto de número 3088, de 21 de junho de 1999 estabelece,  como diretriz para fixação do regime de política monetária, a sistemática de "metas para inflação". Entre as medidas dentro da sistemática estabelicida, destaca-se:','5c94e110e81d930eb8cf4769','a meta é relativa ao resultado da média aritmética dos três principais indices de preço do IBGE.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ee','Em relação à política de metas de Inflação, o papel do Banco Central do Brasil é','5c94e110e81d930eb8cf476b','perseguir a execução da meta de Inflação, através dos instrumentos de política monetária.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ee','Em relação à política de metas de Inflação, o papel do Banco Central do Brasil é','5c94e110e81d930eb8cf476c','discutir com a sociedade as metas de Inflação.')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ee','Em relação à política de metas de Inflação, o papel do Banco Central do Brasil é','5c94e110e81d930eb8cf476d','Definir a metas de Inflação')
insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ee','Em relação à política de metas de Inflação, o papel do Banco Central do Brasil é','5c94e110e81d930eb8cf476e','Ajustar a política de regulação bancária visando primariamente o atingimento da meta de inflação.')
