insert into bdq_old_04_02 values ('5ba8852703579766846d4568','Macroeconomia','5c657cd4fffc5157aa87b64e','Inflação e Política Monetária','5c9e6fed3d3bb1636ac540ef','Como principal instrumento de política monetária, o Copom define:','5c94e110e81d930eb8cf4772','A taxa Selic e taxas de depósito compulsório')
