insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30cc','Tanto  CETIP (Central de Custódia e Liquidação Financeira de Títulos), quanto a SELIC (Sistema de Liquidação e Custódia) correspondem a sistemas em que são feitas a custódia a liquidação da operação de títulos. Sobre esses sistemas, assinale a opção CORRETA:','5bed78d9edbf464d768b30c8','A custódia e a liquidação com títulos públicos federais podem ser tantas no SELIC, quanto na CETIP, cabendo às partes envolvidas no negócio realizar a escola do sistema a ser utilizado')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30cc','Tanto  CETIP (Central de Custódia e Liquidação Financeira de Títulos), quanto a SELIC (Sistema de Liquidação e Custódia) correspondem a sistemas em que são feitas a custódia a liquidação da operação de títulos. Sobre esses sistemas, assinale a opção CORRETA:','5bed78d9edbf464d768b30c9','Os títulos negociados na SELIC são estruturais, o que pratica elimina os riscos relativos a extravio, roubo ou falsificação dos papéis negociados naquele sistema.')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30cc','Tanto  CETIP (Central de Custódia e Liquidação Financeira de Títulos), quanto a SELIC (Sistema de Liquidação e Custódia) correspondem a sistemas em que são feitas a custódia a liquidação da operação de títulos. Sobre esses sistemas, assinale a opção CORRETA:','5bed78d9edbf464d768b30ca','A liquidação das operações realizadas na CETIP são feitas exclusivamente pela Centralizadora de Compensação de Cheques e Outros Papéis')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30cc','Tanto  CETIP (Central de Custódia e Liquidação Financeira de Títulos), quanto a SELIC (Sistema de Liquidação e Custódia) correspondem a sistemas em que são feitas a custódia a liquidação da operação de títulos. Sobre esses sistemas, assinale a opção CORRETA:','5bed78d9edbf464d768b30cb',' A CETIP custodia e promove a liquidação tanto dos CDBs( Certificação de Depósito Bancário) ao portador quanto ao CDBs normativos.')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30d1','Os títulos custodiados, registrados e liquidados na CETIP são chamados de:','5bed78d9edbf464d768b30cd','As ações negociadas em bolsa')
