insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30d1','Os títulos custodiados, registrados e liquidados na CETIP são chamados de:','5bed78d9edbf464d768b30cf',' LFT ( Letra Financeira do Tesouro)')
