insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30d6','Selic é um sistema informatizado que cuida da liquidação e custódia de:','5bed78d9edbf464d768b30d3',' LFT (Letra Financeira do Tesouro )')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30d6','Selic é um sistema informatizado que cuida da liquidação e custódia de:','5bed78d9edbf464d768b30d4',' Ações Negociadas em Bolsa<br />')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30d6','Selic é um sistema informatizado que cuida da liquidação e custódia de:','5bed78d9edbf464d768b30d5',' Contratos de Derivativos<br />')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30db','O Selic- Sistema Especial de Liquidação e Custódia- foi desenvolvido em 1979 pelo Banco Central e pela antiga ANDIMA (Associação Nacional das Instituições do Mercado Aberto) com a finalidade de:','5bed78d9edbf464d768b30d7','Custodiar os títulos públicos e privados negociados no mercado aberto antes da sua liquidação financeira')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30db','O Selic- Sistema Especial de Liquidação e Custódia- foi desenvolvido em 1979 pelo Banco Central e pela antiga ANDIMA (Associação Nacional das Instituições do Mercado Aberto) com a finalidade de:','5bed78d9edbf464d768b30d8','Liquidar financeiramente as ações negociadas no mercado de Bolsa de Valores e custodiar os títulos públicos<br />')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30db','O Selic- Sistema Especial de Liquidação e Custódia- foi desenvolvido em 1979 pelo Banco Central e pela antiga ANDIMA (Associação Nacional das Instituições do Mercado Aberto) com a finalidade de:','5bed78d9edbf464d768b30d9','Regular e fiscalizar os CDBs<br />')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30db','O Selic- Sistema Especial de Liquidação e Custódia- foi desenvolvido em 1979 pelo Banco Central e pela antiga ANDIMA (Associação Nacional das Instituições do Mercado Aberto) com a finalidade de:','5bed78d9edbf464d768b30da','Controlar e liquidar financeiramente as operações de compra e venda de títulos públicos e manter sua custodia fiscal e escritural.<br />')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e0','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5bed78d9edbf464d768b30dc','conte com lastro para as operações contratadas entre o BC e o Tesouro. ')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e0','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5bed78d9edbf464d768b30dd','efetue a emissão, o resgate, o pagamento dos juros e a custódia dos títulos sob sua responsabilidade.')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e0','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5bed78d9edbf464d768b30de','seja composto, entre outros, por títulos da dívida externa brasileira.')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e0','Com relação ao Sistema Especial de Liquidação e Custódia (SELIC), é correto afirmar que:','5bed78d9edbf464d768b30df','eja composto, entre outros, por títulos da dívida externa brasileira.')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e5','O Título indexado ao Tesouro Selic é chamado de:','5bed78d9edbf464d768b30e1','À taxa SELIC e negociado ao par, com ágio ou deságio ')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e5','O Título indexado ao Tesouro Selic é chamado de:','5bed78d9edbf464d768b30e2','À taxa CETIP e negociado ao par, com ágio ou deságio ')
insert into bdq_old_04_02 values ('5ba8852803579766846d45d8','Sistema Financeiro Nacional','5bed7740edbf464d768b30b6','Supervisão bancária','5bed78d9edbf464d768b30e5','O Título indexado ao Tesouro Selic é chamado de:','5bed78d9edbf464d768b30e3',' À Taxa SELIC e negociado ao par, sem ágio ou deságio ')
