insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7c','Sobre a construção da ETTJ brasileira, é correto afirmar que:','5ca2c37339c14562936fba13','As expectativas do mercado sobre os próximos passos do COPOM e as perspectivas de liquidez do governo para prazos mais longos dão forma à curva de juros.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7c','Sobre a construção da ETTJ brasileira, é correto afirmar que:','5ca2c37339c14562936fba14','As expectativas do mercado sobre as taxas DI over formam as curvas de juros pré e as decisões do COPOM definem o shape da curva.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7c','Sobre a construção da ETTJ brasileira, é correto afirmar que:','5ca2c37339c14562936fba15','O mercado forma a curva nominal a partir das negociações com o DI futuro, sendo que a forma da curva de juros é dada pelas expectativas dos ciclos de recessão ou expansão da economia, adicionando prêmios de liquidez para quanto menor for o prazo do vértice considerado.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7c','Sobre a construção da ETTJ brasileira, é correto afirmar que:','5ca2c37339c14562936fba16','A ETTJ é criada a partir de modelos de previsão aplicados pela ANBIMA que a divulga em seu site para consulta de forma gratuita.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7d','Qual é a importância do contrato de DI Futuro para a formação da curva de juros nominal?','5ca2c37339c14562936fba18','São contratos que refletem as expectativas do mercado sobre as taxas de juros futuras para cada vértice de negociação disponível. ')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7d','Qual é a importância do contrato de DI Futuro para a formação da curva de juros nominal?','5ca2c37339c14562936fba19','São contratos que refletem as expectativas do Banco Central sobre os ciclos econômicos de cada vértice de negociação disponível, que quando interpolados, formam a curva de juros nominal e real.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7d','Qual é a importância do contrato de DI Futuro para a formação da curva de juros nominal?','5ca2c37339c14562936fba1a','São contratos negociados para um período de 3 anos, aonde a média ponderada das taxas negociadas dão origem às taxas de cada vértice.')
insert into bdq_old_04_03 values ('5ba254d5ce24ad046ea697ee','Mercado de Renda Fixa','5c3cd434a6cc377e1f07dc56','Estrutura a Termo da Taxa de Juros','5ca2cce339c14562936fbb7d','Qual é a importância do contrato de DI Futuro para a formação da curva de juros nominal?','5ca2c37339c14562936fba1b','Os contratos de DI futuro não servem para que o mercado possa realizar projeções sobre os juros futuros para vários prazos. As taxas negociadas são calculadas com a média ponderada entre cada um dos vértices dando forma à curva de juros.')
